import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MonteurDashboard.css';

const MonteurDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [timeEntries, setTimeEntries] = useState([]);
  const [orders, setOrders] = useState([]);
  const [vacationRequests, setVacationRequests] = useState([]);
  const [dailyReports, setDailyReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isWorking, setIsWorking] = useState(false);
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [workStartTime, setWorkStartTime] = useState(null);
  const [workTotalSeconds, setWorkTotalSeconds] = useState(0);
  const [workEvents, setWorkEvents] = useState([]);
  const [isOnCall, setIsOnCall] = useState(false);
  const [weather, setWeather] = useState({
    temperature: 22,
    condition: 'Sonnig',
    icon: '☀️',
    city: 'München'
  });
  const [nextOrder, setNextOrder] = useState({
    time: '14:30',
    type: 'Wartung',
    location: 'München Zentrum',
    remainingTime: '2:15h'
  });
  const [orderStats, setOrderStats] = useState({
    open: 3,
    completed: 5,
    total: 8,
    byType: {
      'Wartung': { total: 4, open: 2 },
      'Reparatur': { total: 3, open: 1 },
      'Montage': { total: 1, open: 0 }
    }
  });

  // New state for work time tracking
  const [timeView, setTimeView] = useState('daily'); // 'daily' or 'hourly'
  const [entriesTab, setEntriesTab] = useState('time'); // 'time' or 'absence'
  const [absenceForm, setAbsenceForm] = useState({
    type: '',
    startDate: '',
    endDate: '',
    isHalfDay: false,
    comment: ''
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    loadData();
    loadWeather();
    loadWorkState();
    return () => clearInterval(timer);
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [timeResponse, ordersResponse, vacationResponse, reportsResponse] = await Promise.all([
        axios.get('http://localhost:8080/api/monteur/time-entries', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/orders', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/vacation-requests', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/daily-reports', { withCredentials: true })
      ]);

      setTimeEntries(timeResponse.data.time_entries || []);
      setOrders(ordersResponse.data.orders || []);
      setVacationRequests(vacationResponse.data.vacation_requests || []);
      setDailyReports(reportsResponse.data.daily_reports || []);
    } catch (error) {
      console.error('Fehler beim Laden der Daten:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadWeather = async () => {
    // Demo-Wetter-Daten (in echtem System würde hier API-Call stehen)
    setWeather({
      temperature: 22,
      condition: 'Sonnig',
      icon: '☀️',
      city: 'München'
    });
  };

  const loadWorkState = () => {
    const savedStart = localStorage.getItem('workStartTime');
    const savedTotal = localStorage.getItem('workTotalSeconds');
    const savedEvents = localStorage.getItem('workEvents');

    if (savedStart) setWorkStartTime(parseInt(savedStart));
    if (savedTotal) setWorkTotalSeconds(parseInt(savedTotal));
    if (savedEvents) setWorkEvents(JSON.parse(savedEvents));
  };

  const saveWorkState = () => {
    localStorage.setItem('workStartTime', workStartTime?.toString() || '');
    localStorage.setItem('workTotalSeconds', workTotalSeconds.toString());
    localStorage.setItem('workEvents', JSON.stringify(workEvents));
  };

  const handleClockIn = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-in', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(true);
        const now = Date.now();
        setWorkStartTime(now);
        setWorkEvents([...workEvents, { type: 'start', time: new Date(now) }]);
        saveWorkState();
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Einstempeln:', error);
    }
  };

  const handleClockOut = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-out', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(false);
        const now = Date.now();
        const workTime = workStartTime ? Math.floor((now - workStartTime) / 1000) : 0;
        setWorkTotalSeconds(workTotalSeconds + workTime);
        setWorkEvents([...workEvents, { type: 'stop', time: new Date(now) }]);
        setWorkStartTime(null);
        saveWorkState();
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Ausstempeln:', error);
    }
  };

  const handleStartBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/start-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(true);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenstart:', error);
    }
  };

  const handleEndBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/end-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(false);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenende:', error);
    }
  };

  const handleAbsenceSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/absence-request', absenceForm, {
        withCredentials: true
      });
      if (response.data.success) {
        alert('Abwesenheitsantrag erfolgreich gestellt!');
        setAbsenceForm({ type: '', startDate: '', endDate: '', isHalfDay: false, comment: '' });
        loadData(); // Refresh vacation requests
      } else {
        alert('Fehler beim Stellen des Abwesenheitsantrags: ' + response.data.message);
      }
    } catch (error) {
      console.error('Fehler beim Absenden des Abwesenheitsantrags:', error);
      alert('Fehler beim Absenden des Abwesenheitsantrags.');
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('de-DE', { 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('de-DE', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDuration = (seconds) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = seconds % 60;
    return `${h}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}h`;
  };

  const getCurrentWorkTime = () => {
    let total = workTotalSeconds;
    if (workStartTime) {
      total += Math.floor((Date.now() - workStartTime) / 1000);
    }
    return total;
  };

  const getWorkWarning = () => {
    const currentTime = getCurrentWorkTime();
    const maxTime = 8.5 * 3600; // 8.5 Stunden in Sekunden
    const warningTime = 8 * 3600 + 1800; // 8 Stunden + 30 Minuten

    if (currentTime >= warningTime && currentTime < maxTime) {
      return 'Achtung: In 30 Minuten ist die maximale Arbeitszeit (8,5h) erreicht!';
    }
    return null;
  };

  const getTotalWorkTime = () => {
    let totalSeconds = 0;
    timeEntries.forEach(entry => {
      if (entry.total_hours) {
        totalSeconds += entry.total_hours * 3600;
      }
    });
    return totalSeconds;
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <div className="welcome-section">
        <h2>Willkommen, {user.name}!</h2>
        <p className="current-time">{formatDate(currentTime)} - {formatTime(currentTime)}</p>
      </div>

      {/* Störungen */}
      <div className="disturbances-section">
        <div className="disturbance-card">
          <div className="disturbance-header">
            <span className="disturbance-icon">🚨</span>
            <span className="disturbance-label">Störung!</span>
          </div>
          <div className="disturbance-title">Aufzug steckt fest</div>
          <div className="disturbance-details">
            <p><strong>Adresse:</strong> München Zentrum, Marienplatz 1</p>
            <p><strong>Bis:</strong> 18:00 Uhr</p>
          </div>
          <div className="disturbance-actions">
            <button className="navigation-btn">Navigation starten</button>
            <button className="mark-done-btn">Als erledigt markieren</button>
          </div>
        </div>
      </div>

      {/* Hauptbereich: Aufträge und Arbeitszeit */}
      <div className="main-dashboard-grid">
        {/* Meine Aufträge heute */}
        <div className="orders-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            <span>Meine Aufträge heute</span>
          </div>
          <div className="orders-count">{orderStats.open}</div>
          <div className="orders-subtitle">noch zu erledigen</div>
          <div className="orders-stats">
            <span>Erledigt: <strong>{orderStats.completed}</strong></span>
            <span>Gesamt: <strong>{orderStats.total}</strong></span>
          </div>
          <div className="orders-by-type">
            {Object.entries(orderStats.byType).map(([type, stats]) => (
              <div key={type} className="order-type">
                <span className="type-name">{type}:</span>
                <span className="type-stats">
                  Erledigt: <span className="completed">{stats.total - stats.open}</span> | 
                  Offen: <span className="open">{stats.open}</span>
                </span>
              </div>
            ))}
          </div>
          <button className="orders-btn">Zu meinen Aufträgen</button>
        </div>

        {/* Nächster Auftrag */}
        <div className="next-order-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            <span>Nächster Auftrag</span>
          </div>
          <div className="next-order-time">{nextOrder.time}</div>
          <div className="next-order-subtitle">Startzeit</div>
          <div className="next-order-details">
            <p><strong>Art:</strong> {nextOrder.type}</p>
            <p><strong>Standort:</strong> {nextOrder.location}</p>
          </div>
          <div className="next-order-remaining">
            Verbleibende Zeit: <strong>{nextOrder.remainingTime}</strong>
          </div>
          <button className="navigation-btn">Navigation starten</button>
          <button className="firework-btn">🎆 Feuerwerk!</button>
        </div>
      </div>

      {/* Arbeitszeit-Kachel */}
      <div className="work-time-tile">
        <div className="work-time-left">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 17v-2a4 4 0 014-4h0a4 4 0 014 4v2"/>
            </svg>
            <span>Arbeitszeit</span>
          </div>
          <div className="work-time-display">{formatDuration(getCurrentWorkTime())}</div>
          <div className="work-time-buttons">
            {!isWorking ? (
              <button onClick={handleClockIn} className="start-btn">Start</button>
            ) : (
              <button onClick={handleClockOut} className="stop-btn">Stop</button>
            )}
          </div>
          <div className="on-call-checkbox">
            <input 
              type="checkbox" 
              id="on-call-check" 
              checked={isOnCall}
              onChange={(e) => setIsOnCall(e.target.checked)}
            />
            <label htmlFor="on-call-check">Notdienstwoche</label>
          </div>
          {getWorkWarning() && (
            <div className="work-warning">{getWorkWarning()}</div>
          )}
        </div>

        <div className="work-time-right">
          <div className="work-summary-box">
            <div className="work-summary-header">
              <div className="work-date">{currentTime.toLocaleDateString('de-DE')}</div>
              <div className="work-status">{isOnCall ? 'Notdienst' : 'Normal'}</div>
            </div>
            <div className="work-summary-title">Zusammenfassung</div>
            <div className="work-history">
              {workEvents.map((event, index) => (
                <div key={index} className="work-event">
                  <span className="event-time">{formatTime(event.time)}</span>
                  <span className="event-type">{event.type === 'start' ? 'Start' : 'Stop'}</span>
                </div>
              ))}
            </div>
            <div className="work-stats">
              <div className="work-stat">
                <span>Arbeitszeit gesamt:</span>
                <span>{formatDuration(getCurrentWorkTime())}</span>
              </div>
              <div className="work-stat">
                <span>Pausenzeit gesamt:</span>
                <span>0:00h</span>
              </div>
              <div className="work-stat">
                <span>Geplantes Ende (8,5h):</span>
                <span>17:30</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Tageszusammenfassung und Wetter */}
      <div className="summary-weather-grid">
        <div className="daily-summary-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3"/>
            </svg>
            <span>Tageszusammenfassung</span>
          </div>
          <div className="summary-content">
            <ul className="summary-list">
              <li>
                <span className="summary-label">Benachrichtigungen:</span>
                {vacationRequests.filter(req => req.status === 'pending').length > 0 ? (
                  <span className="summary-warning">
                    {vacationRequests.filter(req => req.status === 'pending').length} offene Genehmigungen
                  </span>
                ) : (
                  <span>Keine neuen Benachrichtigungen.</span>
                )}
              </li>
              <li>
                <span className="summary-label">Offene Aufgaben:</span>
                <span>{timeEntries.filter(entry => !entry.clock_out).length} offene Zeiteinträge</span>
              </li>
              <li>
                <span className="summary-label">Anstehende Urlaube:</span>
                <span>{vacationRequests.filter(req => req.status === 'approved').length} geplant</span>
              </li>
            </ul>
            <div className="summary-orders">
              <div className="summary-orders-title">Meine Aufträge heute</div>
              <div className="summary-orders-stats">
                <span>Erledigt: <strong>{orderStats.completed}</strong></span>
                <span>Offen: <strong>{orderStats.open}</strong></span>
              </div>
              <div className="summary-orders-next">
                <span>Nächster Auftrag: <strong>{nextOrder.time}</strong></span>
                <span>Verbleibende Zeit: <strong>{nextOrder.remainingTime}</strong></span>
              </div>
            </div>
            <div className="summary-update">
              Letzte Aktualisierung: {currentTime.toLocaleDateString('de-DE')} {currentTime.toLocaleTimeString('de-DE')}
            </div>
          </div>
        </div>

        {/* Wetter-Widget */}
        <div className="weather-tile">
          <div className="weather-header">
            <svg className="weather-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 2a10 10 0 100 20 10 10 0 000-20zm0 4v4l3 3"/>
            </svg>
            <h3>Wetter am Standort</h3>
          </div>
          <div className="weather-content">
            <div className="weather-location">{weather.city}</div>
            <div className="weather-temperature">{weather.temperature}°C</div>
            <div className="weather-condition">{weather.condition}</div>
            <div className="weather-emoji">{weather.icon}</div>
          </div>
        </div>
      </div>

      {/* Kleinere Kacheln */}
      <div className="small-tiles-grid">
        {/* Offene Zeiteinträge */}
        <div className="small-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            <span>Offene Zeiteinträge</span>
          </div>
          <div className="tile-count">{timeEntries.filter(entry => !entry.clock_out).length}</div>
          <div className="tile-entries">
            {timeEntries.filter(entry => !entry.clock_out).slice(0, 3).map((entry, index) => (
              <div key={index} className="entry-item">
                <span className="entry-time">{entry.clock_in}</span>
                <span className="entry-location">{entry.location || 'Standort'}</span>
              </div>
            ))}
          </div>
          <button className="tile-btn">Zur Zeiterfassung</button>
        </div>

        {/* Letzte Zeiteinträge */}
        <div className="small-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v4a1 1 0 001 1h3m10-5v4a1 1 0 01-1 1h-3m-4 4h4m-2 0v2m0-2v-2"/>
            </svg>
            <span>Letzte Zeiteinträge</span>
          </div>
          <div className="tile-entries">
            {timeEntries.slice(0, 3).map((entry, index) => (
              <div key={index} className="entry-item">
                <span className="entry-time">{entry.clock_in}</span>
                <span className="entry-location">{entry.location || 'Standort'}</span>
              </div>
            ))}
          </div>
          <button className="tile-btn">Alle Zeiteinträge</button>
        </div>

        {/* Resturlaub */}
        <div className="small-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 9V7a5 5 0 00-10 0v2a5 5 0 0010 0zm-2 4v2a3 3 0 01-6 0v-2"/>
            </svg>
            <span>Resturlaub</span>
          </div>
          <div className="tile-count">25 Tage</div>
          <div className="vacation-details">
            <div>Verbraucht: <strong>5 Tage</strong></div>
            <div>Geplant: <strong>3 Tage</strong></div>
            <div>Übrig: <strong>17 Tage</strong></div>
          </div>
          <button className="tile-btn">Zur Urlaubsübersicht</button>
        </div>
      </div>

      {/* Dino Game */}
      <div className="dino-game-tile">
        <h3>Dino-Game</h3>
        <div className="dino-game-container">
          <canvas id="dino-canvas" width="380" height="120"></canvas>
          <div className="dino-score">0</div>
          <div className="dino-instruction">Klick oder Tippen zum Springen</div>
        </div>
      </div>
    </div>
  );

  const renderTimeTracking = () => (
    <div className="time-tracking-content">
      <h2>Zeiterfassung</h2>

      <div className="time-controls">
        <div className="control-buttons">
          {!isWorking ? (
            <button onClick={handleClockIn} className="clock-in-btn">
              Einstempeln
            </button>
          ) : (
            <button onClick={handleClockOut} className="clock-out-btn">
              Ausstempeln
            </button>
          )}
        </div>

        {isWorking && (
          <div className="break-controls">
            {!isOnBreak ? (
              <button onClick={handleStartBreak} className="break-start-btn">
                Pause starten
              </button>
            ) : (
              <button onClick={handleEndBreak} className="break-end-btn">
                Pause beenden
              </button>
            )}
          </div>
        )}
      </div>

      <div className="time-entries">
        <h3>Heutige Einträge</h3>
        {timeEntries
          .filter(entry => new Date(entry.date).toDateString() === currentTime.toDateString())
          .map((entry, index) => (
            <div key={index} className="time-entry">
              <div className="entry-date">
                {new Date(entry.date).toLocaleDateString('de-DE')}
              </div>
              <div className="entry-time">
                <span className="start-time">
                  {entry.clock_in}
                </span>
                {entry.clock_out && (
                  <span className="end-time">
                    - {entry.clock_out}
                  </span>
                )}
              </div>
              <div className="entry-duration">
                {entry.total_hours || 0}h
              </div>
              <div className="entry-status">
                {entry.status}
              </div>
            </div>
          ))}
      </div>
    </div>
  );

  const renderWorkTime = () => {
    const [currentStatus, setCurrentStatus] = useState(null);
    const [workSummary, setWorkSummary] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [message, setMessage] = useState('');

    // Load current status and work summary
    useEffect(() => {
      loadCurrentStatus();
      loadWorkSummary();
    }, []);

    const loadCurrentStatus = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/monteur/current-status');
        if (response.data.success) {
          setCurrentStatus(response.data);
        }
      } catch (error) {
        console.error('Error loading current status:', error);
      }
    };

    const loadWorkSummary = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/monteur/work-summary?days=7');
        if (response.data.success) {
          setWorkSummary(response.data);
        }
      } catch (error) {
        console.error('Error loading work summary:', error);
      }
    };

    const handleClockIn = async () => {
      setIsLoading(true);
      setMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/clock-in');
        if (response.data.success) {
          setMessage(response.data.message);
          loadCurrentStatus();
          loadTimeEntries();
        } else {
          setMessage(response.data.error);
        }
      } catch (error) {
        setMessage('Fehler beim Einstempeln');
      }
      setIsLoading(false);
    };

    const handleClockOut = async () => {
      setIsLoading(true);
      setMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/clock-out');
        if (response.data.success) {
          setMessage(response.data.message);
          loadCurrentStatus();
          loadTimeEntries();
        } else {
          setMessage(response.data.error);
        }
      } catch (error) {
        setMessage('Fehler beim Ausstempeln');
      }
      setIsLoading(false);
    };

    const handleStartBreak = async () => {
      setIsLoading(true);
      setMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/start-break');
        if (response.data.success) {
          setMessage(response.data.message);
          loadCurrentStatus();
        } else {
          setMessage(response.data.error);
        }
      } catch (error) {
        setMessage('Fehler beim Pause starten');
      }
      setIsLoading(false);
    };

    const handleEndBreak = async () => {
      setIsLoading(true);
      setMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/end-break');
        if (response.data.success) {
          setMessage(response.data.message);
          loadCurrentStatus();
        } else {
          setMessage(response.data.error);
        }
      } catch (error) {
        setMessage('Fehler beim Pause beenden');
      }
      setIsLoading(false);
    };

    const getStatusText = () => {
      if (!currentStatus) return 'Lade Status...';
      if (!currentStatus.is_working) return 'Ausgestempelt';
      if (currentStatus.break_status === 'on_break') return 'Pause';
      return 'Eingestempelt';
    };

    const getStatusColor = () => {
      if (!currentStatus) return 'gray';
      if (!currentStatus.is_working) return 'red';
      if (currentStatus.break_status === 'on_break') return 'orange';
      return 'green';
    };

    return (
      <div className="work-time-page">
        <div className="work-time-header">
          <h2>Arbeitszeit</h2>
          <p>Verwalten Sie Ihre Arbeitszeiten und Pausen</p>
        </div>

        {/* Current Status */}
        <div className="current-status-section">
          <div className="status-card">
            <div className="status-indicator" style={{ backgroundColor: getStatusColor() }}>
              <span className="status-text">{getStatusText()}</span>
            </div>
            {currentStatus?.current_entry && (
              <div className="current-entry-info">
                <p><strong>Eingestempelt:</strong> {currentStatus.current_entry.clock_in}</p>
                {currentStatus.current_entry.break_start && (
                  <p><strong>Pause gestartet:</strong> {currentStatus.current_entry.break_start}</p>
                )}
                {currentStatus.current_entry.break_end && (
                  <p><strong>Pause beendet:</strong> {currentStatus.current_entry.break_end}</p>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Work Controls */}
        <div className="work-controls-section">
          <div className="control-buttons">
            <button 
              className={`control-btn ${currentStatus?.is_working ? 'disabled' : 'primary'}`}
              onClick={handleClockIn}
              disabled={currentStatus?.is_working || isLoading}
            >
              {isLoading ? 'Lade...' : 'Einstempeln'}
            </button>

            <button 
              className={`control-btn ${!currentStatus?.is_working ? 'disabled' : 'secondary'}`}
              onClick={handleClockOut}
              disabled={!currentStatus?.is_working || isLoading}
            >
              {isLoading ? 'Lade...' : 'Ausstempeln'}
            </button>

            <button 
              className={`control-btn ${currentStatus?.break_status === 'on_break' ? 'disabled' : 'warning'}`}
              onClick={handleStartBreak}
              disabled={!currentStatus?.is_working || currentStatus?.break_status === 'on_break' || isLoading}
            >
              {isLoading ? 'Lade...' : 'Pause starten'}
            </button>

            <button 
              className={`control-btn ${currentStatus?.break_status !== 'on_break' ? 'disabled' : 'success'}`}
              onClick={handleEndBreak}
              disabled={currentStatus?.break_status !== 'on_break' || isLoading}
            >
              {isLoading ? 'Lade...' : 'Pause beenden'}
            </button>
          </div>

          {message && (
            <div className={`message ${message.includes('Fehler') ? 'error' : 'success'}`}>
              {message}
            </div>
          )}
        </div>

        {/* Work Summary */}
        {workSummary && (
          <div className="work-summary-section">
            <h3>Arbeitszeit-Zusammenfassung (7 Tage)</h3>
            <div className="summary-cards">
              <div className="summary-card">
                <div className="summary-value">{workSummary.total_hours}h</div>
                <div className="summary-label">Gesamtstunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.regular_hours}h</div>
                <div className="summary-label">Reguläre Stunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.overtime_hours}h</div>
                <div className="summary-label">Überstunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.entries_count}</div>
                <div className="summary-label">Einträge</div>
              </div>
            </div>
          </div>
        )}

        {/* Time Entries List */}
        <div className="time-entries-section">
          <h3>Zeiteinträge</h3>
          <div className="entries-table-container">
            <table className="entries-table">
              <thead>
                <tr>
                  <th>Datum</th>
                  <th>Einstempel</th>
                  <th>Ausstempel</th>
                  <th>Pause</th>
                  <th>Arbeitszeit</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {timeEntries.map(entry => (
                  <tr key={entry.id} className="entry-row">
                    <td>{formatDate(entry.date)}</td>
                    <td>{entry.clock_in ? formatTime(entry.clock_in) : '-'}</td>
                    <td>{entry.clock_out ? formatTime(entry.clock_out) : '-'}</td>
                    <td>
                      {entry.break_start && entry.break_end ? 
                        `${formatTime(entry.break_start)} - ${formatTime(entry.break_end)}` : 
                        entry.break_start ? `${formatTime(entry.break_start)} - ...` : '-'
                      }
                    </td>
                    <td>
                      {entry.total_hours ? `${entry.total_hours}h` : '-'}
                      {entry.overtime_hours > 0 && (
                        <span className="overtime-badge">+{entry.overtime_hours}h</span>
                      )}
                    </td>
                    <td>
                      <span className={`status-badge ${entry.status}`}>
                        {entry.status === 'active' ? 'Aktiv' : 
                         entry.status === 'completed' ? 'Abgeschlossen' : entry.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };

  const renderOrders = () => (
    <div className="orders-content">
      <h2>Aufträge & Tagesberichte</h2>

      <div className="daily-report-form">
        <h3>Tagesbericht erstellen</h3>
        <form className="report-form">
          <div className="form-row">
            <div className="form-group">
              <label>Datum:</label>
              <input type="date" className="form-input" defaultValue={currentTime.toISOString().split('T')[0]} />
            </div>
            <div className="form-group">
              <label>Einsatzort:</label>
              <input type="text" className="form-input" placeholder="Adresse..." />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Fabriknummer:</label>
              <input type="text" className="form-input" placeholder="12345" />
            </div>
            <div className="form-group">
              <label>Tätigkeit:</label>
              <select className="form-input">
                <option value="">Wählen...</option>
                <option value="wartung">Wartung</option>
                <option value="reparatur">Reparatur</option>
                <option value="montage">Montage</option>
                <option value="buerot">Bürotätigkeit</option>
              </select>
            </div>
          </div>
          <div className="form-group">
            <label>Arbeitsbeschreibung:</label>
            <textarea className="form-input" rows="4" placeholder="Detaillierte Beschreibung der durchgeführten Arbeiten..."></textarea>
          </div>
          <button type="submit" className="submit-btn">Tagesbericht erstellen</button>
        </form>
      </div>

      <div className="orders-list">
        <h3>Meine Aufträge</h3>
        {orders.map((order, index) => (
          <div key={index} className={`order-card ${order.status}`}>
            <div className="order-header">
              <h3>Auftrag #{order.id}</h3>
              <span className={`status-badge ${order.status}`}>
                {order.status === 'assigned' ? 'Zugewiesen' : 
                 order.status === 'in_progress' ? 'In Bearbeitung' : 
                 order.status === 'completed' ? 'Abgeschlossen' : order.status}
              </span>
            </div>

            <div className="order-details">
              <p><strong>Titel:</strong> {order.title}</p>
              <p><strong>Standort:</strong> {order.location}</p>
              <p><strong>Beschreibung:</strong> {order.description}</p>
              <p><strong>Priorität:</strong> {order.priority}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="reports-list">
        <h3>Meine Tagesberichte</h3>
        {dailyReports.map((report, index) => (
          <div key={index} className={`report-card ${report.status}`}>
            <div className="report-header">
              <h4>Tagesbericht #{report.id}</h4>
              <span className={`status-badge ${report.status}`}>
                {report.status === 'pending' ? 'Ausstehend' : 
                 report.status === 'approved' ? 'Genehmigt' : 'Abgelehnt'}
              </span>
            </div>
            <div className="report-details">
              <p><strong>Datum:</strong> {report.date}</p>
              <p><strong>Arbeitszeit:</strong> {report.hours_worked}h</p>
              <p><strong>Beschreibung:</strong> {report.work_description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderProfile = () => (
    <div className="profile-content">
      <h2>Profil</h2>

      <div className="profile-info">
        <div className="info-group">
          <label>Name:</label>
          <span>{user.name}</span>
        </div>

        <div className="info-group">
          <label>E-Mail:</label>
          <span>{user.email}</span>
        </div>

        <div className="info-group">
          <label>Rolle:</label>
          <span>{user.role}</span>
        </div>
      </div>

      <button onClick={onLogout} className="logout-btn">
        Abmelden
      </button>
    </div>
  );

  return (
    <div className="monteur-dashboard">
      {/* Global Header - Top Menu Bar */}
      <header className="global-header">
        <div className="top-menu-bar">
          <div className="header-left">
            <div className="logo-container">
              <img src="/images/lackner-logo.png" alt="Lackner Aufzüge" className="header-logo" />
              <h1 className="header-title">Lackner Aufzüge</h1>
            </div>
          </div>

          <div className="header-center">
            <div className="current-datetime">
              <div className="current-date">{formatDate(currentTime)}</div>
              <div className="current-time">{formatTime(currentTime)}</div>
            </div>
          </div>

          <div className="header-right">
            <div className="user-info">
              <span className="user-name">{user.name}</span>
              <span className="user-role">{user.role}</span>
            </div>
            <button onClick={onLogout} className="header-logout-btn">
              Abmelden
            </button>
          </div>
        </div>

        {/* Bottom Navigation Bar */}
        <nav className="bottom-nav-bar">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z"/>
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 5a2 2 0 012-2h4a2 2 0 012 2v6H8V5z"/>
            </svg>
            Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'time-tracking' ? 'active' : ''}`}
            onClick={() => setActiveTab('time-tracking')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"/>
            </svg>
            Zeiterfassung
          </button>
          <button 
            className={`nav-btn ${activeTab === 'work-time' ? 'active' : ''}`}
            onClick={() => setActiveTab('work-time')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"/>
            </svg>
            Arbeitszeit
          </button>
          <button 
            className={`nav-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            Aufträge
          </button>
          <button 
            className={`nav-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"/>
            </svg>
            Profil
          </button>
        </nav>
      </header>

      <div className="dashboard-body">
        <main className="dashboard-main">
          {loading && <div className="loading">Lade...</div>}

          {!loading && (
            <>
              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'time-tracking' && renderTimeTracking()}
              {activeTab === 'work-time' && renderWorkTime()}
              {activeTab === 'orders' && renderOrders()}
              {activeTab === 'profile' && renderProfile()}
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default MonteurDashboard;
