              className={errors.breakTime ? 'error' : ''}
            >
              {breakTimeOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.breakTime && <span className="error-message">{errors.breakTime}</span>}
