import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ArbeitszeitEingabe.css';

const API_BASE_URL = process.env.NODE_ENV === 'production' 
  ? 'https://zeiterfassung-app-1754516418.azurewebsites.net' 
  : 'http://localhost:8080';

const ArbeitszeitEingabe = () => {
  const [timeEntries, setTimeEntries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [editingEntry, setEditingEntry] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Formular-State
  const [formData, setFormData] = useState({
    start_time: '',
    end_time: '',
    description: '',
    order_id: ''
  });

  // Lade Zeiteinträge beim Start
  useEffect(() => {
    loadTimeEntries();
  }, []);

  const loadTimeEntries = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/api/monteur/time-entries`, {
        withCredentials: true
      });
      if (response.data.success) {
        setTimeEntries(response.data.entries || []);
      }
    } catch (error) {
      console.error('Fehler beim Laden der Zeiteinträge:', error);
      setMessage('Fehler beim Laden der Zeiteinträge');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const resetForm = () => {
    setFormData({
      start_time: '',
      end_time: '',
      description: '',
      order_id: ''
    });
    setEditingEntry(null);
    setShowForm(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      let response;
      if (editingEntry) {
        // Update existing entry
        response = await axios.put(
          `${API_BASE_URL}/api/monteur/time-entries/${editingEntry.id}`,
          formData,
          { withCredentials: true }
        );
      } else {
        // Create new entry
        response = await axios.post(
          `${API_BASE_URL}/api/monteur/time-entries`,
          formData,
          { withCredentials: true }
        );
      }

      if (response.data.success) {
        setMessage(editingEntry ? 'Eintrag erfolgreich aktualisiert!' : 'Eintrag erfolgreich erstellt!');
        resetForm();
        loadTimeEntries();
      } else {
        setMessage('Fehler: ' + (response.data.message || 'Unbekannter Fehler'));
      }
    } catch (error) {
      console.error('Fehler beim Speichern:', error);
      setMessage('Fehler beim Speichern: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (entry) => {
    setEditingEntry(entry);
    setFormData({
      start_time: entry.start_time ? entry.start_time.substring(0, 16) : '',
      end_time: entry.end_time ? entry.end_time.substring(0, 16) : '',
      description: entry.description || '',
      order_id: entry.order_id || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (entryId) => {
    if (!window.confirm('Möchten Sie diesen Eintrag wirklich löschen?')) {
      return;
    }

    setLoading(true);
    try {
      const response = await axios.delete(
        `${API_BASE_URL}/api/monteur/time-entries/${entryId}`,
        { withCredentials: true }
      );

      if (response.data.success) {
        setMessage('Eintrag erfolgreich gelöscht!');
        loadTimeEntries();
      } else {
        setMessage('Fehler beim Löschen: ' + (response.data.message || 'Unbekannter Fehler'));
      }
    } catch (error) {
      console.error('Fehler beim Löschen:', error);
      setMessage('Fehler beim Löschen: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const formatDateTime = (dateTimeString) => {
    if (!dateTimeString) return '-';
    const date = new Date(dateTimeString);
    return date.toLocaleString('de-DE');
  };

  const calculateDuration = (startTime, endTime) => {
    if (!startTime || !endTime) return '-';
    const start = new Date(startTime);
    const end = new Date(endTime);
    const diffMs = end - start;
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMinutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));
    return `${diffHours}h ${diffMinutes}min`;
  };

  return (
    <div className="arbeitszeit-container">
      <div className="arbeitszeit-header">
        <h1>Arbeitszeit Erfassung</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          Neuer Eintrag
        </button>
      </div>

      {message && (
        <div className={`message ${message.includes('Fehler') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      {/* Eingabeformular */}
      {showForm && (
        <div className="form-container">
          <h2>{editingEntry ? 'Eintrag bearbeiten' : 'Neuer Zeiteintrag'}</h2>
          <form onSubmit={handleSubmit} className="time-entry-form">
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="start_time">Startzeit *</label>
                <input
                  type="datetime-local"
                  id="start_time"
                  name="start_time"
                  value={formData.start_time}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label htmlFor="end_time">Endzeit</label>
                <input
                  type="datetime-local"
                  id="end_time"
                  name="end_time"
                  value={formData.end_time}
                  onChange={handleInputChange}
                  className="form-input"
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="description">Beschreibung</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Was wurde gemacht?"
                className="form-input"
                rows="3"
              />
            </div>

            <div className="form-group">
              <label htmlFor="order_id">Auftragsnummer</label>
              <input
                type="text"
                id="order_id"
                name="order_id"
                value={formData.order_id}
                onChange={handleInputChange}
                placeholder="z.B. AUF-001"
                className="form-input"
              />
            </div>

            <div className="form-actions">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Speichere...' : (editingEntry ? 'Aktualisieren' : 'Erstellen')}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={resetForm}
                disabled={loading}
              >
                Abbrechen
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Zeiteinträge Tabelle */}
      <div className="entries-container">
        <h2>Zeiteinträge</h2>
        {loading ? (
          <div className="loading">Lade Zeiteinträge...</div>
        ) : timeEntries.length === 0 ? (
          <div className="no-entries">Keine Zeiteinträge vorhanden</div>
        ) : (
          <div className="entries-table">
            <table>
              <thead>
                <tr>
                  <th>Startzeit</th>
                  <th>Endzeit</th>
                  <th>Dauer</th>
                  <th>Beschreibung</th>
                  <th>Auftrag</th>
                  <th>Aktionen</th>
                </tr>
              </thead>
              <tbody>
                {timeEntries.map((entry) => (
                  <tr key={entry.id}>
                    <td>{formatDateTime(entry.start_time)}</td>
                    <td>{formatDateTime(entry.end_time)}</td>
                    <td>{calculateDuration(entry.start_time, entry.end_time)}</td>
                    <td>{entry.description || '-'}</td>
                    <td>{entry.order_id || '-'}</td>
                    <td>
                      <button 
                        className="btn btn-small btn-secondary"
                        onClick={() => handleEdit(entry)}
                      >
                        Bearbeiten
                      </button>
                      <button 
                        className="btn btn-small btn-danger"
                        onClick={() => handleDelete(entry.id)}
                      >
                        Löschen
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default ArbeitszeitEingabe;
