import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MonteurDashboard.css';

const MonteurDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [timeEntries, setTimeEntries] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isWorking, setIsWorking] = useState(false);
  const [currentEntry, setCurrentEntry] = useState(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    loadData();
    return () => clearInterval(timer);
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [timeResponse, ordersResponse] = await Promise.all([
        axios.get('/api/monteur/time-entries', { withCredentials: true }),
        axios.get('/api/monteur/orders', { withCredentials: true })
      ]);

      setTimeEntries(timeResponse.data.time_entries || []);
      setOrders(ordersResponse.data.orders || []);
    } catch (error) {
      console.error('Fehler beim Laden der Daten:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStartWork = async () => {
    try {
      const response = await axios.post('/api/monteur/start-work', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(true);
        setCurrentEntry(response.data.time_entry);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Arbeitsbeginn:', error);
    }
  };

  const handleStopWork = async () => {
    try {
      const response = await axios.post('/api/monteur/stop-work', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(false);
        setCurrentEntry(null);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Arbeitsende:', error);
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('de-DE', { 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('de-DE', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <div className="welcome-section">
        <h2>Willkommen, {user.name}!</h2>
        <p className="current-time">{formatDate(currentTime)} - {formatTime(currentTime)}</p>
      </div>

      <div className="work-controls">
        <div className="work-status">
          <h3>Arbeitsstatus</h3>
          <div className={`status-indicator ${isWorking ? 'working' : 'not-working'}`}>
            {isWorking ? 'Arbeite gerade' : 'Nicht am Arbeiten'}
          </div>
        </div>

        <div className="work-buttons">
          {!isWorking ? (
            <button onClick={handleStartWork} className="start-btn">
              Arbeitsbeginn
            </button>
          ) : (
            <button onClick={handleStopWork} className="stop-btn">
              Arbeitsende
            </button>
          )}
        </div>
      </div>

      <div className="quick-stats">
        <div className="stat-card">
          <h4>Heute gearbeitet</h4>
          <p className="stat-value">
            {timeEntries.filter(entry => 
              new Date(entry.start_time).toDateString() === currentTime.toDateString()
            ).reduce((total, entry) => {
              const start = new Date(entry.start_time);
              const end = entry.end_time ? new Date(entry.end_time) : currentTime;
              return total + (end - start);
            }, 0) / (1000 * 60 * 60) | 0}h
          </p>
        </div>

        <div className="stat-card">
          <h4>Aktive Aufträge</h4>
          <p className="stat-value">
            {orders.filter(order => order.status === 'in_progress').length}
          </p>
        </div>

        <div className="stat-card">
          <h4>Offene Aufträge</h4>
          <p className="stat-value">
            {orders.filter(order => order.status === 'pending').length}
          </p>
        </div>
      </div>
    </div>
  );

  const renderTimeTracking = () => (
    <div className="time-tracking-content">
      <h2>Zeiterfassung</h2>

      <div className="time-entries">
        <h3>Heutige Einträge</h3>
        {timeEntries
          .filter(entry => new Date(entry.start_time).toDateString() === currentTime.toDateString())
          .map((entry, index) => (
            <div key={index} className="time-entry">
              <div className="entry-time">
                <span className="start-time">
                  {new Date(entry.start_time).toLocaleTimeString('de-DE', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </span>
                {entry.end_time && (
                  <span className="end-time">
                    - {new Date(entry.end_time).toLocaleTimeString('de-DE', { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </span>
                )}
              </div>
              <div className="entry-duration">
                {entry.end_time ? 
                  `${Math.round((new Date(entry.end_time) - new Date(entry.start_time)) / (1000 * 60))} min` :
                  'Läuft...'
                }
              </div>
            </div>
          ))}
      </div>
    </div>
  );

  const renderOrders = () => (
    <div className="orders-content">
      <h2>Aufträge</h2>

      <div className="orders-list">
        {orders.map((order, index) => (
          <div key={index} className={`order-card ${order.status}`}>
            <div className="order-header">
              <h3>Auftrag #{order.id}</h3>
              <span className={`status-badge ${order.status}`}>
                {order.status === 'pending' ? 'Offen' : 
                 order.status === 'in_progress' ? 'In Bearbeitung' : 
                 order.status === 'completed' ? 'Abgeschlossen' : order.status}
              </span>
            </div>

            <div className="order-details">
              <p><strong>Kunde:</strong> {order.customer_name}</p>
              <p><strong>Adresse:</strong> {order.address}</p>
              <p><strong>Beschreibung:</strong> {order.description}</p>
              <p><strong>Priorität:</strong> {order.priority}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderProfile = () => (
    <div className="profile-content">
      <h2>Profil</h2>

      <div className="profile-info">
        <div className="info-group">
          <label>Name:</label>
          <span>{user.name}</span>
        </div>

        <div className="info-group">
          <label>E-Mail:</label>
          <span>{user.email}</span>
        </div>

        <div className="info-group">
          <label>Rolle:</label>
          <span>{user.role}</span>
        </div>
      </div>

      <button onClick={onLogout} className="logout-btn">
        Abmelden
      </button>
    </div>
  );

  return (
    <div className="monteur-dashboard">
      <header className="dashboard-header">
        <div className="header-left">
          <h1>Lackner Aufzüge</h1>
          <span className="user-role">Monteur Dashboard</span>
        </div>

        <div className="header-right">
          <span className="user-name">{user.name}</span>
          <button onClick={onLogout} className="header-logout-btn">
            Abmelden
          </button>
        </div>
      </header>

      <div className="dashboard-body">
        <nav className="dashboard-nav">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'time-tracking' ? 'active' : ''}`}
            onClick={() => setActiveTab('time-tracking')}
          >
            Zeiterfassung
          </button>
          <button 
            className={`nav-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            Aufträge
          </button>
          <button 
            className={`nav-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Profil
          </button>
        </nav>

        <main className="dashboard-main">
          {loading && <div className="loading">Lade...</div>}

          {!loading && (
            <>
              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'time-tracking' && renderTimeTracking()}
              {activeTab === 'orders' && renderOrders()}
              {activeTab === 'profile' && renderProfile()}
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default MonteurDashboard;
