import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ArbeitszeitEingabe from './ArbeitszeitEingabe';
import ZeiterfassungsFilter from './ZeiterfassungsFilter';
import ZeiteintraegeTabelle from './ZeiteintraegeTabelle';
import './MonteurDashboard.css';

const MonteurDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [timeEntries, setTimeEntries] = useState([]);
  const [orders, setOrders] = useState([]);
  const [vacationRequests, setVacationRequests] = useState([]);
  const [dailyReports, setDailyReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isWorking, setIsWorking] = useState(false);
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [workStartTime, setWorkStartTime] = useState(null);
  const [workTotalSeconds, setWorkTotalSeconds] = useState(0);
  const [workEvents, setWorkEvents] = useState([]);
  const [isOnCall, setIsOnCall] = useState(false);
  const [weather, setWeather] = useState({
    temperature: 22,
    condition: 'Sonnig',
    icon: '☀️',
    city: 'München'
  });
  const [nextOrder, setNextOrder] = useState({
    time: '14:30',
    type: 'Wartung',
    location: 'München Zentrum',
    remainingTime: '2:15h'
  });
  const [orderStats, setOrderStats] = useState({
    open: 3,
    completed: 7,
    total: 10,
    byType: {
      'Wartung': { open: 2, completed: 4 },
      'Reparatur': { open: 1, completed: 2 },
      'Installation': { open: 0, completed: 1 }
    }
  });
  const [absenceForm, setAbsenceForm] = useState({
    type: 'vacation',
    startDate: '',
    endDate: '',
    isHalfDay: false,
    comment: ''
  });

  // Work Time Page States
  const [currentStatus, setCurrentStatus] = useState(null);
  const [workSummary, setWorkSummary] = useState(null);
  const [workTimeLoading, setWorkTimeLoading] = useState(false);
  const [workTimeMessage, setWorkTimeMessage] = useState('');

  // Orders Page States
  const [ordersData, setOrdersData] = useState([]);
  const [nextOrderData, setNextOrderData] = useState(null);
  const [orderStatsData, setOrderStatsData] = useState({});
  const [ordersLoading, setOrdersLoading] = useState(false);
  const [map, setMap] = useState(null);

  // State für Arbeitszeit-Seite
  const [filterData, setFilterData] = useState({
    type: 'today',
    startDate: new Date().toISOString().split('T')[0],
    endDate: new Date().toISOString().split('T')[0]
  });

  const handleFilterChange = (newFilterData) => {
    setFilterData(newFilterData);
    // Hier würde die API mit den neuen Filter-Daten aufgerufen werden
