              </button>
            )}
          </div>
        )}
      </div>

      <div className="time-entries">
        <h3>Heutige Einträge</h3>
        {timeEntries
          .filter(entry => new Date(entry.date).toDateString() === currentTime.toDateString())
          .map((entry, index) => (
            <div key={index} className="time-entry">
              <div className="entry-date">
                {new Date(entry.date).toLocaleDateString('de-DE')}
              </div>
              <div className="entry-time">
                <span className="start-time">
                  {entry.clock_in}
                </span>
                {entry.clock_out && (
                  <span className="end-time">
                    - {entry.clock_out}
                  </span>
                )}
              </div>
              <div className="entry-duration">
                {entry.total_hours || 0}h
              </div>
              <div className="entry-status">
                {entry.status}
              </div>
            </div>
          ))}
      </div>
    </div>
  );

  const renderWorkTime = () => {
    return (
      <div className="arbeitszeit-page">
        <div className="page-header">
          <h2>Arbeitszeit</h2>
          <p>Verwalten Sie Ihre Arbeitszeiten und Pausen</p>
        </div>

        {/* Eingabebereich */}
        <ArbeitszeitEingabe 
          onTimeEntrySaved={handleTimeEntrySaved}
          currentStatus={currentStatus}
        />

        {/* Filterbereich */}
        <ZeiterfassungsFilter 
          onFilterChange={handleFilterChange}
        />

        {/* Übersicht */}
        <div className="entries-section">
          <h3>Zeiteinträge</h3>
          <ZeiteintraegeTabelle 
            entries={timeEntries}
            onEditEntry={handleEditEntry}
          />
        </div>
      </div>
    );
  };

  const renderOrders = () => {
    const handleOrderStatusChange = async (orderId, newStatus) => {
      try {
        // Hier würde die API-Aktualisierung stattfinden
        console.log(`Order ${orderId} status changed to ${newStatus}`);

        // Aktualisiere lokalen State
        setOrdersData(prevOrders => 
          prevOrders.map(order => 
            order.id === orderId 
              ? { ...order, status: newStatus }
              : order
          )
        );

        // Lade Daten neu
        loadOrdersData();
      } catch (error) {
        console.error('Error updating order status:', error);
      }
    };

    const openNavigation = (coordinates) => {
      if (coordinates && coordinates.length === 2) {
        const [lat, lng] = coordinates;
        const url = `https://www.openstreetmap.org/?mlat=${lat}&mlon=${lng}#map=15/${lat}/${lng}`;
        window.open(url, '_blank');
      }
    };

    return (
      <div className="orders-page">
        <div className="orders-header">
          <h2>Meine Aufträge heute</h2>
          <p>Verwalten Sie Ihre täglichen Aufträge und Standorte</p>
        </div>

        {loading ? (
          <div className="loading">Lade Aufträge...</div>
        ) : (
          <>
            {/* Auftrags-Statistiken */}
            <div className="orders-stats-section">
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon">📋</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStatsData.open}</div>
                    <div className="stat-label">Offene Aufträge</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">✅</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStatsData.completed}</div>
                    <div className="stat-label">Erledigt</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">📊</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStatsData.total}</div>
                    <div className="stat-label">Gesamt</div>
                  </div>
                </div>
              </div>

              {/* Auftragsarten-Breakdown */}
              <div className="orders-breakdown">
                <h3>Nach Auftragsart</h3>
                <div className="breakdown-grid">
                  {Object.entries(orderStatsData.byType).map(([type, stats]) => (
                    <div key={type} className="breakdown-item">
                      <span className="type-name">{type}:</span>
                      <span className="type-stats">
                        Erledigt: <span className="completed">{stats.total - stats.open}</span> | 
                        Offen: <span className="open">{stats.open}</span>
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Nächster Auftrag mit Karte */}
            {nextOrderData && (
              <div className="next-order-section">
                <h3>Nächster Auftrag</h3>
                <div className="next-order-card">
                  <div className="order-info">
                    <div className="order-header">
                      <h4>Auftrag #{nextOrderData.id}</h4>
                      <span className={`priority-badge ${nextOrderData.priority}`}>
                        {nextOrderData.priority === 'high' ? 'Hoch' : 
                         nextOrderData.priority === 'urgent' ? 'Dringend' : 'Normal'}
                      </span>
                    </div>
                    <div className="order-details">
                      <p><strong>Titel:</strong> {nextOrderData.title}</p>
                      <p><strong>Standort:</strong> {nextOrderData.location}</p>
                      <p><strong>Startzeit:</strong> {nextOrderData.start_time}</p>
                      <p><strong>Beschreibung:</strong> {nextOrderData.description}</p>
                    </div>
                    <div className="order-actions">
                      <button 
                        className="action-btn primary"
                        onClick={() => handleOrderStatusChange(nextOrderData.id, 'in_progress')}
                      >
                        Auftrag starten
                      </button>
                      <button 
                        className="action-btn secondary"
                        onClick={() => openNavigation(nextOrderData.coordinates)}
                      >
                        Navigation starten
                      </button>
                    </div>
                  </div>
                  <div className="order-map">
                    <div id="order-map" className="map-container"></div>
                  </div>
                </div>
              </div>
            )}

            {/* Alle Aufträge Liste */}
            <div className="orders-list-section">
              <h3>Alle Aufträge</h3>
              <div className="orders-grid">
                {ordersData.map(order => (
                  <div key={order.id} className={`order-card ${order.status}`}>
                    <div className="order-card-header">
                      <h4>Auftrag #{order.id}</h4>
                      <div className="order-badges">
                        <span className={`status-badge ${order.status}`}>
                          {order.status === 'assigned' ? 'Zugewiesen' : 
                           order.status === 'in_progress' ? 'In Bearbeitung' : 
                           order.status === 'completed' ? 'Abgeschlossen' : order.status}
                        </span>
                        <span className={`priority-badge ${order.priority}`}>
                          {order.priority === 'high' ? 'Hoch' : 
                           order.priority === 'urgent' ? 'Dringend' : 'Normal'}
                        </span>
                      </div>
                    </div>

                    <div className="order-card-content">
                      <p><strong>Titel:</strong> {order.title}</p>
                      <p><strong>Standort:</strong> {order.location}</p>
                      <p><strong>Startzeit:</strong> {order.start_time}</p>
                      <p><strong>Typ:</strong> {order.type}</p>
                      <p><strong>Beschreibung:</strong> {order.description}</p>
                    </div>

                    <div className="order-card-actions">
                      {order.status === 'assigned' && (
                        <button 
                          className="action-btn primary"
                          onClick={() => handleOrderStatusChange(order.id, 'in_progress')}
                        >
                          Starten
                        </button>
                      )}
                      {order.status === 'in_progress' && (
                        <button 
                          className="action-btn success"
                          onClick={() => handleOrderStatusChange(order.id, 'completed')}
                        >
                          Abschließen
                        </button>
                      )}
                      <button 
                        className="action-btn secondary"
                        onClick={() => openNavigation(order.coordinates)}
                      >
                        Navigation
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    );
  };

  const renderProfile = () => (
    <div className="profile-content">
      <h2>Profil</h2>

      <div className="profile-info">
        <div className="info-group">
          <label>Name:</label>
          <span>{user.name}</span>
        </div>

        <div className="info-group">
          <label>E-Mail:</label>
          <span>{user.email}</span>
        </div>

        <div className="info-group">
          <label>Rolle:</label>
          <span>{user.role}</span>
        </div>
      </div>

      <button onClick={onLogout} className="logout-btn">
        Abmelden
      </button>
    </div>
  );

  return (
    <div className="monteur-dashboard">
      {/* Global Header - Top Menu Bar */}
      <header className="global-header">
        <div className="top-menu-bar">
          <div className="header-left">
            <div className="logo-container">
              <div className="logo-text">LA</div>
              <h1 className="header-title">Lackner Aufzüge</h1>
            </div>
          </div>

          <div className="header-center">
            <div className="current-datetime">
              <div className="current-date">{formatDate(currentTime)}</div>
              <div className="current-time">{formatTime(currentTime)}</div>
            </div>
          </div>

          <div className="header-right">
            <div className="user-info">
              <span className="user-name">{user.name}</span>
              <span className="user-role">{user.role}</span>
            </div>
            <button onClick={onLogout} className="header-logout-btn">
              Abmelden
            </button>
          </div>
        </div>

        {/* Bottom Navigation Bar */}
        <nav className="bottom-nav-bar">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z"/>
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 5a2 2 0 012-2h4a2 2 0 012 2v6H8V5z"/>
            </svg>
            Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'time-tracking' ? 'active' : ''}`}
            onClick={() => setActiveTab('time-tracking')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"/>
            </svg>
            Zeiterfassung
          </button>
          <button 
            className={`nav-btn ${activeTab === 'work-time' ? 'active' : ''}`}
            onClick={() => setActiveTab('work-time')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"/>
            </svg>
            Arbeitszeit
          </button>
          <button 
            className={`nav-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            Aufträge
          </button>
          <button 
            className={`nav-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"/>
            </svg>
            Profil
          </button>
        </nav>
      </header>

      <div className="dashboard-body">
        <main className="dashboard-main">
          {loading && <div className="loading">Lade...</div>}

          {!loading && (
            <>
              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'time-tracking' && renderTimeTracking()}
              {activeTab === 'work-time' && renderWorkTime()}
              {activeTab === 'orders' && renderOrders()}
              {activeTab === 'profile' && renderProfile()}
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default MonteurDashboard;
