  const renderWorkTime = () => {
    const formatDate = (dateString) => {
      const date = new Date(dateString);
      return date.toLocaleDateString('de-DE');
    };

    const formatTime = (timeString) => {
      if (!timeString) return '-';
      const date = new Date(timeString);
      return date.toLocaleTimeString('de-DE', { hour: '2-digit', minute: '2-digit' });
    };

    const formatDuration = (seconds) => {
      const hours = Math.floor(seconds / 3600);
      const minutes = Math.floor((seconds % 3600) / 60);
      return `${hours}:${minutes.toString().padStart(2, '0')}h`;
    };

    const getWorkTimeStats = () => {
      if (!timeEntries.length) return {
        totalHours: 0,
        regularHours: 0,
        overtimeHours: 0,
        averageHours: 0,
        daysWorked: 0
      };

      const today = new Date();
      const weekStart = new Date(today);
      weekStart.setDate(today.getDate() - today.getDay());
      weekStart.setHours(0, 0, 0, 0);

      const weekEntries = timeEntries.filter(entry => {
        const entryDate = new Date(entry.clock_in);
        return entryDate >= weekStart && entry.total_hours;
      });

      const totalHours = weekEntries.reduce((sum, entry) => sum + entry.total_hours, 0);
      const regularHours = weekEntries.length * 8; // 8h pro Tag
      const overtimeHours = Math.max(0, totalHours - regularHours);
      const averageHours = weekEntries.length > 0 ? totalHours / weekEntries.length : 0;

      return {
        totalHours: totalHours.toFixed(1),
        regularHours: regularHours.toFixed(1),
        overtimeHours: overtimeHours.toFixed(1),
        averageHours: averageHours.toFixed(1),
        daysWorked: weekEntries.length
      };
    };

    const stats = getWorkTimeStats();

    return (
      <div className="work-time-page">
        <div className="page-header">
          <h2>Arbeitszeit</h2>
          <p>Ãœbersicht und Statistiken der Arbeitszeit</p>
        </div>

        {/* Arbeitszeit-Statistiken */}
