  const formatDate = (date) => {
    return date.toLocaleDateString('de-DE', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDuration = (seconds) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = seconds % 60;
    return `${h}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}h`;
  };

  const getCurrentWorkTime = () => {
    let total = workTotalSeconds;
    if (workStartTime) {
      total += Math.floor((Date.now() - workStartTime) / 1000);
    }
    return total;
  };

  const getWorkWarning = () => {
    const currentTime = getCurrentWorkTime();
    const maxTime = 8.5 * 3600; // 8.5 Stunden in Sekunden
    const warningTime = 8 * 3600 + 1800; // 8 Stunden + 30 Minuten

    if (currentTime >= warningTime && currentTime < maxTime) {
      return 'Achtung: In 30 Minuten ist die maximale Arbeitszeit (8,5h) erreicht!';
    }
    return null;
  };

