import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MonteurDashboard.css';

const MonteurDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [timeEntries, setTimeEntries] = useState([]);
  const [orders, setOrders] = useState([]);
  const [vacationRequests, setVacationRequests] = useState([]);
  const [dailyReports, setDailyReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isWorking, setIsWorking] = useState(false);
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [workStartTime, setWorkStartTime] = useState(null);
  const [workTotalSeconds, setWorkTotalSeconds] = useState(0);
  const [workEvents, setWorkEvents] = useState([]);
  const [isOnCall, setIsOnCall] = useState(false);
  const [weather, setWeather] = useState({
    temperature: 22,
    condition: 'Sonnig',
    icon: '☀️',
    city: 'München'
  });
  const [nextOrder, setNextOrder] = useState({
    time: '14:30',
    type: 'Wartung',
    location: 'München Zentrum',
    remainingTime: '2:15h'
  });
  const [orderStats, setOrderStats] = useState({
    open: 3,
    completed: 5,
    total: 8,
    byType: {
      'Wartung': { total: 4, open: 2 },
      'Reparatur': { total: 3, open: 1 },
      'Montage': { total: 1, open: 0 }
    }
  });

  // New state for work time tracking
  const [timeView, setTimeView] = useState('daily'); // 'daily' or 'hourly'
  const [entriesTab, setEntriesTab] = useState('time'); // 'time' or 'absence'
  const [absenceForm, setAbsenceForm] = useState({
    type: '',
    startDate: '',
    endDate: '',
    isHalfDay: false,
    comment: ''
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    loadData();
    loadWeather();
    loadWorkState();
    return () => clearInterval(timer);
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [timeResponse, ordersResponse, vacationResponse, reportsResponse] = await Promise.all([
        axios.get('http://localhost:8080/api/monteur/time-entries', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/orders', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/vacation-requests', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/daily-reports', { withCredentials: true })
      ]);

      setTimeEntries(timeResponse.data.time_entries || []);
      setOrders(ordersResponse.data.orders || []);
      setVacationRequests(vacationResponse.data.vacation_requests || []);
      setDailyReports(reportsResponse.data.daily_reports || []);
    } catch (error) {
      console.error('Fehler beim Laden der Daten:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadWeather = async () => {
    // Demo-Wetter-Daten (in echtem System würde hier API-Call stehen)
    setWeather({
      temperature: 22,
      condition: 'Sonnig',
      icon: '☀️',
      city: 'München'
    });
  };

  const loadWorkState = () => {
    const savedStart = localStorage.getItem('workStartTime');
    const savedTotal = localStorage.getItem('workTotalSeconds');
    const savedEvents = localStorage.getItem('workEvents');

    if (savedStart) setWorkStartTime(parseInt(savedStart));
    if (savedTotal) setWorkTotalSeconds(parseInt(savedTotal));
    if (savedEvents) setWorkEvents(JSON.parse(savedEvents));
  };

  const saveWorkState = () => {
    localStorage.setItem('workStartTime', workStartTime?.toString() || '');
    localStorage.setItem('workTotalSeconds', workTotalSeconds.toString());
    localStorage.setItem('workEvents', JSON.stringify(workEvents));
  };

  const handleClockIn = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-in', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(true);
        const now = Date.now();
        setWorkStartTime(now);
        setWorkEvents([...workEvents, { type: 'start', time: new Date(now) }]);
        saveWorkState();
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Einstempeln:', error);
    }
  };

  const handleClockOut = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-out', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(false);
        const now = Date.now();
        const workTime = workStartTime ? Math.floor((now - workStartTime) / 1000) : 0;
        setWorkTotalSeconds(workTotalSeconds + workTime);
        setWorkEvents([...workEvents, { type: 'stop', time: new Date(now) }]);
        setWorkStartTime(null);
        saveWorkState();
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Ausstempeln:', error);
    }
  };

  const handleStartBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/start-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(true);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenstart:', error);
    }
  };

  const handleEndBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/end-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(false);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenende:', error);
    }
  };

  const handleAbsenceSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/absence-request', absenceForm, {
        withCredentials: true
      });
      if (response.data.success) {
        alert('Abwesenheitsantrag erfolgreich gestellt!');
        setAbsenceForm({ type: '', startDate: '', endDate: '', isHalfDay: false, comment: '' });
        loadData(); // Refresh vacation requests
      } else {
        alert('Fehler beim Stellen des Abwesenheitsantrags: ' + response.data.message);
      }
    } catch (error) {
      console.error('Fehler beim Absenden des Abwesenheitsantrags:', error);
      alert('Fehler beim Absenden des Abwesenheitsantrags.');
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('de-DE', { 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('de-DE', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDuration = (seconds) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = seconds % 60;
    return `${h}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}h`;
  };

  const getCurrentWorkTime = () => {
    let total = workTotalSeconds;
    if (workStartTime) {
      total += Math.floor((Date.now() - workStartTime) / 1000);
    }
    return total;
  };

  const getWorkWarning = () => {
    const currentTime = getCurrentWorkTime();
    const maxTime = 8.5 * 3600; // 8.5 Stunden in Sekunden
    const warningTime = 8 * 3600 + 1800; // 8 Stunden + 30 Minuten

    if (currentTime >= warningTime && currentTime < maxTime) {
      return 'Achtung: In 30 Minuten ist die maximale Arbeitszeit (8,5h) erreicht!';
    }
    return null;
  };

  const getTotalWorkTime = () => {
    let totalSeconds = 0;
    timeEntries.forEach(entry => {
      if (entry.total_hours) {
        totalSeconds += entry.total_hours * 3600;
      }
    });
    return totalSeconds;
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <div className="welcome-section">
        <h2>Willkommen, {user.name}!</h2>
        <p className="current-time">{formatDate(currentTime)} - {formatTime(currentTime)}</p>
      </div>

      {/* Störungen */}
      <div className="disturbances-section">
        <div className="disturbance-card">
          <div className="disturbance-header">
            <span className="disturbance-icon">🚨</span>
            <span className="disturbance-label">Störung!</span>
          </div>
          <div className="disturbance-title">Aufzug steckt fest</div>
          <div className="disturbance-details">
            <p><strong>Adresse:</strong> München Zentrum, Marienplatz 1</p>
            <p><strong>Bis:</strong> 18:00 Uhr</p>
          </div>
          <div className="disturbance-actions">
            <button className="navigation-btn">Navigation starten</button>
            <button className="mark-done-btn">Als erledigt markieren</button>
          </div>
        </div>
      </div>

      {/* Hauptbereich: Aufträge und Arbeitszeit */}
      <div className="main-dashboard-grid">
        {/* Meine Aufträge heute */}
        <div className="orders-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            <span>Meine Aufträge heute</span>
          </div>
          <div className="orders-count">{orderStats.open}</div>
          <div className="orders-subtitle">noch zu erledigen</div>
          <div className="orders-stats">
            <span>Erledigt: <strong>{orderStats.completed}</strong></span>
            <span>Gesamt: <strong>{orderStats.total}</strong></span>
          </div>
          <div className="orders-by-type">
            {Object.entries(orderStats.byType).map(([type, stats]) => (
              <div key={type} className="order-type">
                <span className="type-name">{type}:</span>
                <span className="type-stats">
                  Erledigt: <span className="completed">{stats.total - stats.open}</span> | 
                  Offen: <span className="open">{stats.open}</span>
                </span>
              </div>
            ))}
          </div>
          <button className="orders-btn">Zu meinen Aufträgen</button>
        </div>

        {/* Nächster Auftrag */}
        <div className="next-order-tile">
          <div className="tile-header">
            <svg className="tile-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
