  const renderWorkTime = () => {
    const handleClockIn = async () => {
      setWorkTimeLoading(true);
      setWorkTimeMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/clock-in');
        if (response.data.success) {
          setWorkTimeMessage(response.data.message);
          loadCurrentStatus();
          loadTimeEntries();
        } else {
          setWorkTimeMessage(response.data.error);
        }
      } catch (error) {
        setWorkTimeMessage('Fehler beim Einstempeln');
      }
      setWorkTimeLoading(false);
    };

    const handleClockOut = async () => {
      setWorkTimeLoading(true);
      setWorkTimeMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/clock-out');
        if (response.data.success) {
          setWorkTimeMessage(response.data.message);
          loadCurrentStatus();
          loadTimeEntries();
        } else {
          setWorkTimeMessage(response.data.error);
        }
      } catch (error) {
        setWorkTimeMessage('Fehler beim Ausstempeln');
      }
      setWorkTimeLoading(false);
    };

    const handleStartBreak = async () => {
      setWorkTimeLoading(true);
      setWorkTimeMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/start-break');
        if (response.data.success) {
          setWorkTimeMessage(response.data.message);
          loadCurrentStatus();
        } else {
          setWorkTimeMessage(response.data.error);
        }
      } catch (error) {
        setWorkTimeMessage('Fehler beim Pause starten');
      }
      setWorkTimeLoading(false);
    };

    const handleEndBreak = async () => {
      setWorkTimeLoading(true);
      setWorkTimeMessage('');
      try {
        const response = await axios.post('http://localhost:8080/api/monteur/end-break');
        if (response.data.success) {
          setWorkTimeMessage(response.data.message);
          loadCurrentStatus();
        } else {
          setWorkTimeMessage(response.data.error);
        }
      } catch (error) {
        setWorkTimeMessage('Fehler beim Pause beenden');
      }
      setWorkTimeLoading(false);
    };

    const getStatusText = () => {
      if (!currentStatus) return 'Lade Status...';
      if (!currentStatus.is_working) return 'Ausgestempelt';
      if (currentStatus.break_status === 'on_break') return 'Pause';
      return 'Eingestempelt';
    };

    const getStatusColor = () => {
      if (!currentStatus) return 'gray';
      if (!currentStatus.is_working) return 'red';
      if (currentStatus.break_status === 'on_break') return 'orange';
      return 'green';
    };

    return (
      <div className="work-time-page">
        <div className="work-time-header">
          <h2>Arbeitszeit</h2>
          <p>Verwalten Sie Ihre Arbeitszeiten und Pausen</p>
        </div>

        {/* Current Status */}
        <div className="current-status-section">
          <div className="status-card">
            <div className="status-indicator" style={{ backgroundColor: getStatusColor() }}>
              <span className="status-text">{getStatusText()}</span>
            </div>
            {currentStatus?.current_entry && (
              <div className="current-entry-info">
                <p><strong>Eingestempelt:</strong> {currentStatus.current_entry.clock_in}</p>
                {currentStatus.current_entry.break_start && (
                  <p><strong>Pause gestartet:</strong> {currentStatus.current_entry.break_start}</p>
                )}
                {currentStatus.current_entry.break_end && (
                  <p><strong>Pause beendet:</strong> {currentStatus.current_entry.break_end}</p>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Work Controls */}
        <div className="work-controls-section">
          <div className="control-buttons">
            <button 
              className={`control-btn ${currentStatus?.is_working ? 'disabled' : 'primary'}`}
              onClick={handleClockIn}
              disabled={currentStatus?.is_working || isLoading}
            >
              {isLoading ? 'Lade...' : 'Einstempeln'}
            </button>

            <button 
              className={`control-btn ${!currentStatus?.is_working ? 'disabled' : 'secondary'}`}
              onClick={handleClockOut}
              disabled={!currentStatus?.is_working || isLoading}
            >
              {isLoading ? 'Lade...' : 'Ausstempeln'}
            </button>

            <button 
              className={`control-btn ${currentStatus?.break_status === 'on_break' ? 'disabled' : 'warning'}`}
              onClick={handleStartBreak}
              disabled={!currentStatus?.is_working || currentStatus?.break_status === 'on_break' || isLoading}
            >
              {isLoading ? 'Lade...' : 'Pause starten'}
            </button>

            <button 
              className={`control-btn ${currentStatus?.break_status !== 'on_break' ? 'disabled' : 'success'}`}
              onClick={handleEndBreak}
              disabled={currentStatus?.break_status !== 'on_break' || isLoading}
            >
              {isLoading ? 'Lade...' : 'Pause beenden'}
            </button>
          </div>

          {message && (
            <div className={`message ${message.includes('Fehler') ? 'error' : 'success'}`}>
              {message}
            </div>
          )}
        </div>

        {/* Work Summary */}
        {workSummary && (
          <div className="work-summary-section">
            <h3>Arbeitszeit-Zusammenfassung (7 Tage)</h3>
            <div className="summary-cards">
              <div className="summary-card">
                <div className="summary-value">{workSummary.total_hours}h</div>
                <div className="summary-label">Gesamtstunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.regular_hours}h</div>
                <div className="summary-label">Reguläre Stunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.overtime_hours}h</div>
                <div className="summary-label">Überstunden</div>
              </div>
              <div className="summary-card">
                <div className="summary-value">{workSummary.entries_count}</div>
                <div className="summary-label">Einträge</div>
              </div>
            </div>
          </div>
        )}

        {/* Time Entries List */}
        <div className="time-entries-section">
          <h3>Zeiteinträge</h3>
          <div className="entries-table-container">
            <table className="entries-table">
              <thead>
                <tr>
                  <th>Datum</th>
                  <th>Einstempel</th>
                  <th>Ausstempel</th>
                  <th>Pause</th>
                  <th>Arbeitszeit</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {timeEntries.map(entry => (
                  <tr key={entry.id} className="entry-row">
                    <td>{formatDate(entry.date)}</td>
                    <td>{entry.clock_in ? formatTime(entry.clock_in) : '-'}</td>
                    <td>{entry.clock_out ? formatTime(entry.clock_out) : '-'}</td>
                    <td>
                      {entry.break_start && entry.break_end ? 
                        `${formatTime(entry.break_start)} - ${formatTime(entry.break_end)}` : 
                        entry.break_start ? `${formatTime(entry.break_start)} - ...` : '-'
                      }
                    </td>
                    <td>
                      {entry.total_hours ? `${entry.total_hours}h` : '-'}
                      {entry.overtime_hours > 0 && (
                        <span className="overtime-badge">+{entry.overtime_hours}h</span>
                      )}
                    </td>
                    <td>
                      <span className={`status-badge ${entry.status}`}>
                        {entry.status === 'active' ? 'Aktiv' : 
                         entry.status === 'completed' ? 'Abgeschlossen' : entry.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };

  const renderOrders = () => {
    const [orders, setOrders] = useState([]);
    const [nextOrder, setNextOrder] = useState(null);
    const [orderStats, setOrderStats] = useState({
      open: 0,
      completed: 0,
      total: 0,
      byType: {}
    });
    const [loading, setLoading] = useState(true);
    const [map, setMap] = useState(null);

    useEffect(() => {
      loadOrders();
    }, []);

    // OpenStreetMap Integration
    useEffect(() => {
      if (nextOrder && nextOrder.coordinates && window.L) {
        // Warte kurz bis das DOM bereit ist
        setTimeout(() => {
          const mapElement = document.getElementById('order-map');
          if (mapElement && !map) {
            const newMap = window.L.map('order-map', {
              zoomControl: false,
              attributionControl: false,
              dragging: true,
              scrollWheelZoom: true,
              doubleClickZoom: true,
              boxZoom: true,
              keyboard: true,
              tap: true,
              touchZoom: true
            });

            // OpenStreetMap Tiles
            window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
              maxZoom: 19
            }).addTo(newMap);

            // Marker für den nächsten Auftrag
            const [lat, lng] = nextOrder.coordinates;
            window.L.marker([lat, lng]).addTo(newMap);

            // Karte auf den Marker zentrieren
            newMap.setView([lat, lng], 14);

            setMap(newMap);
          }
        }, 100);
      }
    }, [nextOrder, map]);

    const loadOrders = async () => {
      try {
        setLoading(true);
        // Simuliere Aufträge (später durch echte API ersetzen)
        const mockOrders = [
          {
            id: 1,
            title: "Wartung Aufzug Hauptstraße 15",
            location: "Hauptstraße 15, München",
            description: "Regelmäßige Wartung des Personenaufzugs",
            priority: "normal",
            status: "assigned",
            start_time: "08:00",
            coordinates: [48.1351, 11.5820],
            type: "Wartung"
          },
          {
            id: 2,
            title: "Reparatur Lastenaufzug Bahnhof",
            location: "Bahnhofplatz 1, München",
            description: "Defekte Türschließung reparieren",
            priority: "high",
            status: "in_progress",
            start_time: "10:30",
            coordinates: [48.1401, 11.5600],
            type: "Reparatur"
          },
          {
            id: 3,
            title: "Inspektion Aufzug Einkaufszentrum",
            location: "Kaufingerstraße 1, München",
            description: "Jährliche Sicherheitsinspektion",
            priority: "normal",
            status: "completed",
            start_time: "14:00",
            coordinates: [48.1375, 11.5754],
            type: "Inspektion"
          }
        ];

        setOrders(mockOrders);

        // Berechne Statistiken
        const stats = {
          open: mockOrders.filter(o => o.status !== 'completed').length,
          completed: mockOrders.filter(o => o.status === 'completed').length,
          total: mockOrders.length,
          byType: {}
        };

        mockOrders.forEach(order => {
          if (!stats.byType[order.type]) {
            stats.byType[order.type] = { total: 0, open: 0 };
          }
          stats.byType[order.type].total++;
          if (order.status !== 'completed') {
            stats.byType[order.type].open++;
          }
        });

        setOrderStats(stats);

        // Setze nächsten Auftrag
        const next = mockOrders.find(o => o.status === 'assigned');
        setNextOrder(next);

        setLoading(false);
      } catch (error) {
        console.error('Error loading orders:', error);
        setLoading(false);
      }
    };

    const handleOrderStatusChange = async (orderId, newStatus) => {
      try {
        // Hier würde die API-Call kommen
        setOrders(prev => prev.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        ));

        // Lade Statistiken neu
        loadOrders();
      } catch (error) {
        console.error('Error updating order status:', error);
      }
    };

    const openNavigation = (coordinates) => {
      const [lat, lng] = coordinates;
      window.open(`https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}`, '_blank');
    };

    return (
      <div className="orders-page">
        <div className="orders-header">
          <h2>Meine Aufträge heute</h2>
          <p>Verwalten Sie Ihre täglichen Aufträge und Standorte</p>
        </div>

        {loading ? (
          <div className="loading">Lade Aufträge...</div>
        ) : (
          <>
            {/* Auftrags-Statistiken */}
            <div className="orders-stats-section">
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon">📋</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStats.open}</div>
                    <div className="stat-label">Offene Aufträge</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">✅</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStats.completed}</div>
                    <div className="stat-label">Erledigt</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">📊</div>
                  <div className="stat-content">
                    <div className="stat-value">{orderStats.total}</div>
                    <div className="stat-label">Gesamt</div>
                  </div>
                </div>
              </div>

              {/* Auftragsarten-Breakdown */}
              <div className="orders-breakdown">
                <h3>Nach Auftragsart</h3>
                <div className="breakdown-grid">
                  {Object.entries(orderStats.byType).map(([type, stats]) => (
                    <div key={type} className="breakdown-item">
                      <span className="type-name">{type}:</span>
                      <span className="type-stats">
                        Erledigt: <span className="completed">{stats.total - stats.open}</span> | 
                        Offen: <span className="open">{stats.open}</span>
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Nächster Auftrag mit Karte */}
            {nextOrder && (
              <div className="next-order-section">
                <h3>Nächster Auftrag</h3>
                <div className="next-order-card">
                  <div className="order-info">
                    <div className="order-header">
                      <h4>Auftrag #{nextOrder.id}</h4>
                      <span className={`priority-badge ${nextOrder.priority}`}>
                        {nextOrder.priority === 'high' ? 'Hoch' : 
                         nextOrder.priority === 'urgent' ? 'Dringend' : 'Normal'}
                      </span>
                    </div>
                    <div className="order-details">
                      <p><strong>Titel:</strong> {nextOrder.title}</p>
                      <p><strong>Standort:</strong> {nextOrder.location}</p>
                      <p><strong>Startzeit:</strong> {nextOrder.start_time}</p>
                      <p><strong>Beschreibung:</strong> {nextOrder.description}</p>
                    </div>
                    <div className="order-actions">
                      <button 
                        className="action-btn primary"
                        onClick={() => handleOrderStatusChange(nextOrder.id, 'in_progress')}
                      >
                        Auftrag starten
                      </button>
                      <button 
                        className="action-btn secondary"
                        onClick={() => openNavigation(nextOrder.coordinates)}
                      >
                        Navigation starten
                      </button>
                    </div>
                  </div>
                  <div className="order-map">
                    <div id="order-map" className="map-container"></div>
                  </div>
                </div>
              </div>
            )}

            {/* Alle Aufträge Liste */}
            <div className="orders-list-section">
              <h3>Alle Aufträge</h3>
              <div className="orders-grid">
                {orders.map(order => (
                  <div key={order.id} className={`order-card ${order.status}`}>
                    <div className="order-card-header">
                      <h4>Auftrag #{order.id}</h4>
                      <div className="order-badges">
                        <span className={`status-badge ${order.status}`}>
                          {order.status === 'assigned' ? 'Zugewiesen' : 
                           order.status === 'in_progress' ? 'In Bearbeitung' : 
                           order.status === 'completed' ? 'Abgeschlossen' : order.status}
                        </span>
                        <span className={`priority-badge ${order.priority}`}>
                          {order.priority === 'high' ? 'Hoch' : 
                           order.priority === 'urgent' ? 'Dringend' : 'Normal'}
                        </span>
                      </div>
                    </div>

                    <div className="order-card-content">
                      <p><strong>Titel:</strong> {order.title}</p>
                      <p><strong>Standort:</strong> {order.location}</p>
                      <p><strong>Startzeit:</strong> {order.start_time}</p>
                      <p><strong>Typ:</strong> {order.type}</p>
                      <p><strong>Beschreibung:</strong> {order.description}</p>
                    </div>

                    <div className="order-card-actions">
                      {order.status === 'assigned' && (
                        <button 
                          className="action-btn primary"
                          onClick={() => handleOrderStatusChange(order.id, 'in_progress')}
                        >
                          Starten
                        </button>
                      )}
                      {order.status === 'in_progress' && (
                        <button 
                          className="action-btn success"
                          onClick={() => handleOrderStatusChange(order.id, 'completed')}
                        >
                          Abschließen
                        </button>
                      )}
                      <button 
                        className="action-btn secondary"
                        onClick={() => openNavigation(order.coordinates)}
                      >
                        Navigation
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    );
  };

  const renderProfile = () => (
    <div className="profile-content">
      <h2>Profil</h2>

      <div className="profile-info">
        <div className="info-group">
          <label>Name:</label>
          <span>{user.name}</span>
        </div>

        <div className="info-group">
          <label>E-Mail:</label>
          <span>{user.email}</span>
        </div>

        <div className="info-group">
          <label>Rolle:</label>
          <span>{user.role}</span>
        </div>
      </div>

      <button onClick={onLogout} className="logout-btn">
        Abmelden
      </button>
    </div>
  );

  return (
    <div className="monteur-dashboard">
      {/* Global Header - Top Menu Bar */}
      <header className="global-header">
        <div className="top-menu-bar">
          <div className="header-left">
            <div className="logo-container">
              <img src="/images/lackner-logo.png" alt="Lackner Aufzüge" className="header-logo" />
              <h1 className="header-title">Lackner Aufzüge</h1>
            </div>
          </div>

          <div className="header-center">
            <div className="current-datetime">
              <div className="current-date">{formatDate(currentTime)}</div>
              <div className="current-time">{formatTime(currentTime)}</div>
            </div>
          </div>

          <div className="header-right">
            <div className="user-info">
              <span className="user-name">{user.name}</span>
              <span className="user-role">{user.role}</span>
            </div>
            <button onClick={onLogout} className="header-logout-btn">
              Abmelden
            </button>
          </div>
        </div>

        {/* Bottom Navigation Bar */}
        <nav className="bottom-nav-bar">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z"/>
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 5a2 2 0 012-2h4a2 2 0 012 2v6H8V5z"/>
            </svg>
            Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'time-tracking' ? 'active' : ''}`}
            onClick={() => setActiveTab('time-tracking')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"/>
            </svg>
            Zeiterfassung
          </button>
          <button 
            className={`nav-btn ${activeTab === 'work-time' ? 'active' : ''}`}
            onClick={() => setActiveTab('work-time')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"/>
            </svg>
            Arbeitszeit
          </button>
          <button 
            className={`nav-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2a4 4 0 014-4h2a4 4 0 014 4v2M9 17H7a4 4 0 01-4-4V7a4 4 0 014-4h10a4 4 0 014 4v6a4 4 0 01-4 4h-2M9 17v2a2 2 0 002 2h2a2 2 0 002-2v-2"/>
            </svg>
            Aufträge
          </button>
          <button 
            className={`nav-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            <svg className="nav-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"/>
            </svg>
            Profil
          </button>
        </nav>
      </header>

      <div className="dashboard-body">
        <main className="dashboard-main">
          {loading && <div className="loading">Lade...</div>}

          {!loading && (
            <>
              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'time-tracking' && renderTimeTracking()}
              {activeTab === 'work-time' && renderWorkTime()}
              {activeTab === 'orders' && renderOrders()}
              {activeTab === 'profile' && renderProfile()}
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default MonteurDashboard;
