import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MonteurDashboard.css';

const MonteurDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [timeEntries, setTimeEntries] = useState([]);
  const [orders, setOrders] = useState([]);
  const [vacationRequests, setVacationRequests] = useState([]);
  const [dailyReports, setDailyReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isWorking, setIsWorking] = useState(false);
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [workSummary, setWorkSummary] = useState({
    total_hours: 0,
    regular_hours: 0,
    overtime_hours: 0,
    entries_count: 0
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    loadData();
    return () => clearInterval(timer);
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [timeResponse, ordersResponse, vacationResponse, reportsResponse] = await Promise.all([
        axios.get('http://localhost:8080/api/monteur/time-entries', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/orders', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/vacation-requests', { withCredentials: true }),
        axios.get('http://localhost:8080/api/monteur/daily-reports', { withCredentials: true })
      ]);

      setTimeEntries(timeResponse.data.time_entries || []);
      setOrders(ordersResponse.data.orders || []);
      setVacationRequests(vacationResponse.data.vacation_requests || []);
      setDailyReports(reportsResponse.data.daily_reports || []);
    } catch (error) {
      console.error('Fehler beim Laden der Daten:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClockIn = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-in', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(true);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Einstempeln:', error);
    }
  };

  const handleClockOut = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/clock-out', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsWorking(false);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Ausstempeln:', error);
    }
  };

  const handleStartBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/start-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(true);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenstart:', error);
    }
  };

  const handleEndBreak = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/monteur/end-break', {}, {
        withCredentials: true
      });

      if (response.data.success) {
        setIsOnBreak(false);
        loadData();
      }
    } catch (error) {
      console.error('Fehler beim Pausenende:', error);
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('de-DE', { 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('de-DE', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <div className="welcome-section">
        <h2>Willkommen, {user.name}!</h2>
        <p className="current-time">{formatDate(currentTime)} - {formatTime(currentTime)}</p>
      </div>

      <div className="work-controls">
        <div className="work-status">
          <h3>Arbeitsstatus</h3>
          <div className={`status-indicator ${isWorking ? 'working' : 'not-working'}`}>
            {isWorking ? 'Arbeite gerade' : 'Nicht am Arbeiten'}
          </div>
          {isWorking && isOnBreak && (
            <div className="status-indicator break">
              Pause läuft
            </div>
          )}
        </div>

        <div className="work-buttons">
          {!isWorking ? (
            <button onClick={handleClockIn} className="start-btn">
              Einstempeln
            </button>
          ) : (
            <div className="button-group">
              <button onClick={handleClockOut} className="stop-btn">
                Ausstempeln
              </button>
              {!isOnBreak ? (
                <button onClick={handleStartBreak} className="break-btn">
                  Pause starten
                </button>
              ) : (
                <button onClick={handleEndBreak} className="break-btn">
                  Pause beenden
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="quick-stats">
        <div className="stat-card">
          <h4>Heute gearbeitet</h4>
          <p className="stat-value">
            {timeEntries.filter(entry => 
              new Date(entry.date).toDateString() === currentTime.toDateString()
            ).reduce((total, entry) => {
              const start = new Date(entry.clock_in);
              const end = entry.clock_out ? new Date(entry.clock_out) : currentTime;
              return total + (end - start);
            }, 0) / (1000 * 60 * 60) | 0}h
          </p>
        </div>

        <div className="stat-card">
          <h4>Aktive Aufträge</h4>
          <p className="stat-value">
            {orders.filter(order => order.status === 'assigned').length}
          </p>
        </div>

        <div className="stat-card">
          <h4>Offene Anträge</h4>
          <p className="stat-value">
            {vacationRequests.filter(req => req.status === 'pending').length}
          </p>
        </div>

        <div className="stat-card">
          <h4>Tagesberichte</h4>
          <p className="stat-value">
            {dailyReports.filter(report => 
              new Date(report.date).toDateString() === currentTime.toDateString()
            ).length}
          </p>
        </div>
      </div>

      <div className="recent-activity">
        <h3>Aktuelle Aktivitäten</h3>
        <div className="activity-list">
          {timeEntries.slice(0, 3).map((entry, index) => (
            <div key={index} className="activity-item">
              <span className="activity-date">{new Date(entry.date).toLocaleDateString('de-DE')}</span>
              <span className="activity-time">
                {entry.clock_in} - {entry.clock_out || 'läuft'}
              </span>
              <span className="activity-duration">
                {entry.total_hours || 0}h
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderTimeTracking = () => (
    <div className="time-tracking-content">
      <h2>Zeiterfassung</h2>

      <div className="time-controls">
        <div className="control-buttons">
          {!isWorking ? (
            <button onClick={handleClockIn} className="clock-in-btn">
              Einstempeln
            </button>
          ) : (
            <button onClick={handleClockOut} className="clock-out-btn">
              Ausstempeln
            </button>
          )}
        </div>

        {isWorking && (
          <div className="break-controls">
            {!isOnBreak ? (
              <button onClick={handleStartBreak} className="break-start-btn">
                Pause starten
              </button>
            ) : (
              <button onClick={handleEndBreak} className="break-end-btn">
                Pause beenden
              </button>
            )}
          </div>
        )}
      </div>

      <div className="time-entries">
        <h3>Heutige Einträge</h3>
        {timeEntries
          .filter(entry => new Date(entry.date).toDateString() === currentTime.toDateString())
          .map((entry, index) => (
            <div key={index} className="time-entry">
              <div className="entry-date">
                {new Date(entry.date).toLocaleDateString('de-DE')}
              </div>
              <div className="entry-time">
                <span className="start-time">
                  {entry.clock_in}
                </span>
                {entry.clock_out && (
                  <span className="end-time">
                    - {entry.clock_out}
                  </span>
                )}
              </div>
              <div className="entry-duration">
                {entry.total_hours || 0}h
              </div>
              <div className="entry-status">
                {entry.status}
              </div>
            </div>
          ))}
      </div>
    </div>
  );

  const renderWorkTime = () => (
    <div className="work-time-content">
      <h2>Arbeitszeit & Abwesenheiten</h2>

      <div className="request-forms">
        <div className="form-section">
          <h3>Urlaub beantragen</h3>
          <form className="request-form">
            <div className="form-row">
              <div className="form-group">
                <label>Von:</label>
                <input type="date" className="form-input" />
              </div>
              <div className="form-group">
                <label>Bis:</label>
                <input type="date" className="form-input" />
              </div>
            </div>
            <div className="form-group">
              <label>Grund:</label>
              <textarea className="form-input" rows="3" placeholder="Urlaubsgrund..."></textarea>
            </div>
            <button type="submit" className="submit-btn">Antrag stellen</button>
          </form>
        </div>

        <div className="form-section">
          <h3>Krankmeldung erstellen</h3>
          <form className="request-form">
            <div className="form-row">
              <div className="form-group">
                <label>Von:</label>
                <input type="date" className="form-input" />
              </div>
              <div className="form-group">
                <label>Bis:</label>
                <input type="date" className="form-input" />
              </div>
            </div>
            <div className="form-group">
              <label>Grund:</label>
              <textarea className="form-input" rows="3" placeholder="Krankheitsgrund..."></textarea>
            </div>
            <button type="submit" className="submit-btn">Krankmeldung erstellen</button>
          </form>
        </div>
      </div>

      <div className="requests-list">
        <h3>Meine Anträge</h3>
        {vacationRequests.map((request, index) => (
          <div key={index} className={`request-item ${request.status}`}>
            <div className="request-header">
              <h4>Urlaubsantrag</h4>
              <span className={`status-badge ${request.status}`}>
                {request.status === 'pending' ? 'Ausstehend' : 
                 request.status === 'approved' ? 'Genehmigt' : 'Abgelehnt'}
              </span>
            </div>
            <div className="request-details">
              <p><strong>Zeitraum:</strong> {request.start_date} - {request.end_date}</p>
              <p><strong>Grund:</strong> {request.reason}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderOrders = () => (
    <div className="orders-content">
      <h2>Aufträge & Tagesberichte</h2>

      <div className="daily-report-form">
        <h3>Tagesbericht erstellen</h3>
        <form className="report-form">
          <div className="form-row">
            <div className="form-group">
              <label>Datum:</label>
              <input type="date" className="form-input" defaultValue={currentTime.toISOString().split('T')[0]} />
            </div>
            <div className="form-group">
              <label>Einsatzort:</label>
              <input type="text" className="form-input" placeholder="Adresse..." />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Fabriknummer:</label>
              <input type="text" className="form-input" placeholder="12345" />
            </div>
            <div className="form-group">
              <label>Tätigkeit:</label>
              <select className="form-input">
                <option value="">Wählen...</option>
                <option value="wartung">Wartung</option>
                <option value="reparatur">Reparatur</option>
                <option value="montage">Montage</option>
                <option value="buerot">Bürotätigkeit</option>
              </select>
            </div>
          </div>
          <div className="form-group">
            <label>Arbeitsbeschreibung:</label>
            <textarea className="form-input" rows="4" placeholder="Detaillierte Beschreibung der durchgeführten Arbeiten..."></textarea>
          </div>
          <button type="submit" className="submit-btn">Tagesbericht erstellen</button>
        </form>
      </div>

      <div className="orders-list">
        <h3>Meine Aufträge</h3>
        {orders.map((order, index) => (
          <div key={index} className={`order-card ${order.status}`}>
            <div className="order-header">
              <h3>Auftrag #{order.id}</h3>
              <span className={`status-badge ${order.status}`}>
                {order.status === 'assigned' ? 'Zugewiesen' : 
                 order.status === 'in_progress' ? 'In Bearbeitung' : 
                 order.status === 'completed' ? 'Abgeschlossen' : order.status}
              </span>
            </div>

            <div className="order-details">
              <p><strong>Titel:</strong> {order.title}</p>
              <p><strong>Standort:</strong> {order.location}</p>
              <p><strong>Beschreibung:</strong> {order.description}</p>
              <p><strong>Priorität:</strong> {order.priority}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="reports-list">
        <h3>Meine Tagesberichte</h3>
        {dailyReports.map((report, index) => (
          <div key={index} className={`report-card ${report.status}`}>
            <div className="report-header">
              <h4>Tagesbericht #{report.id}</h4>
              <span className={`status-badge ${report.status}`}>
                {report.status === 'pending' ? 'Ausstehend' : 
                 report.status === 'approved' ? 'Genehmigt' : 'Abgelehnt'}
              </span>
            </div>
            <div className="report-details">
              <p><strong>Datum:</strong> {report.date}</p>
              <p><strong>Arbeitszeit:</strong> {report.hours_worked}h</p>
              <p><strong>Beschreibung:</strong> {report.work_description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderProfile = () => (
    <div className="profile-content">
      <h2>Profil</h2>

      <div className="profile-info">
        <div className="info-group">
          <label>Name:</label>
          <span>{user.name}</span>
        </div>

        <div className="info-group">
          <label>E-Mail:</label>
          <span>{user.email}</span>
        </div>

        <div className="info-group">
          <label>Rolle:</label>
          <span>{user.role}</span>
        </div>
      </div>

      <button onClick={onLogout} className="logout-btn">
        Abmelden
      </button>
    </div>
  );

  return (
    <div className="monteur-dashboard">
      <header className="dashboard-header">
        <div className="header-left">
          <h1>Lackner Aufzüge</h1>
          <span className="user-role">Monteur Dashboard</span>
        </div>

        <div className="header-right">
          <span className="user-name">{user.name}</span>
          <button onClick={onLogout} className="header-logout-btn">
            Abmelden
          </button>
        </div>
      </header>

      <div className="dashboard-body">
        <nav className="dashboard-nav">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'time-tracking' ? 'active' : ''}`}
            onClick={() => setActiveTab('time-tracking')}
          >
            Zeiterfassung
          </button>
          <button 
            className={`nav-btn ${activeTab === 'work-time' ? 'active' : ''}`}
            onClick={() => setActiveTab('work-time')}
          >
            Arbeitszeit
          </button>
          <button 
            className={`nav-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            Aufträge
          </button>
          <button 
            className={`nav-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Profil
          </button>
        </nav>

        <main className="dashboard-main">
          {loading && <div className="loading">Lade...</div>}

          {!loading && (
            <>
              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'time-tracking' && renderTimeTracking()}
              {activeTab === 'work-time' && renderWorkTime()}
              {activeTab === 'orders' && renderOrders()}
              {activeTab === 'profile' && renderProfile()}
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default MonteurDashboard;
